import React, { Component } from "react";
import "../index.css";
import Text from "../components/mainText";
import Dropdown from "../components/dropdown";
import { gameInfo } from "../Const/gameInfo";

// import setFieldSize from '../components/setFiledSize';
// const time = [30, 45, 90];

const fieldSize = gameInfo.size.data;
const yeild = ["Mongolian Steppe", "European Average", "Belorusian Magic"];

class FieldSetting extends React.Component {
  constructor() {
    super();
    this.state = {
      time: 30000,
      size: 200,
      yeild: 0.6,
    };
  }

  handleSubmitTime(item) {
    localStorage.setItem("time", item);
    console.log(item);
  }

  handleSubmitSize(item) {
    localStorage.setItem("size", item);
  }

  handleSubmitYeild(item) {
    localStorage.setItem("yeild", item);
  }

  render() {
    return (
      <div className="section">
        <form className="raw">
          <label className="main-text">Time:</label>
          <Dropdown
            data={time}
            id="GET-time"
            onChange={this.handleSubmitTime}
          />
        </form>
        <form className="raw">
          <label className="main-text">Field size:</label>
          <Dropdown
            data={fieldSize}
            id="GET-size"
            onChange={this.handleSubmitSize}
          />
        </form>
        <form className="raw">
          <label className="main-text">Yeild:</label>
          <Dropdown
            data={yeild}
            id="GET-yeild"
            onChange={this.handleSubmitYeild}
          />
        </form>
      </div>
    );
  }
}
export default FieldSetting;
