import React, { Component } from 'react';
import PageTitle from '../components/h1';
import MainText from '../components/text';
import SmallText from '../components/smallText';
import AccentText from '../components/accentText';
import Button from '../components/button';
import Tile from '../components/tile';

import GameSettings from '../modules/gameSetting';
import MusicSettings from '../modules/musicSettings';
import InputBlock from '../modules/inputName';

import Coin from '../components/coin';
import Logo from '../components/logo';
import '../index.css';

class MainMenu extends Component {
  render() {
    return (
      <div className="wrapper">
        <div class="control-panel">
          <PageTitle label="Bitcoin Miner ⛏" />
          <div className="subtitle">
            <AccentText label="Tap as hard, as you can" />
          </div>
          <InputBlock />
          <GameSettings />
          <MusicSettings />

          <div className="section-wrapper">
            <div className="settings-item">
              <Button label="Stats" />
              <Button label="Start game" />
            </div>
          </div>

          <div className="footer-wrapper">
            <div className="settings-item">
              <a href="https://rs.school/" target="_blanc">
                <Logo class="logo-footer" />
              </a>
              <a href="https://github.com/ShvetsBy" target="_blanc">
                <SmallText label="Ivan Shvets, 2021" />{' '}
              </a>
            </div>
          </div>
        </div>
        <div className="illustration">
          <div className="flying-bitcoin">
            <Tile className="game-menu-tile" />
            //* <Coin className="game-menu-coin" />
          </div>
        </div>
      </div>
    );
  }
}

export default MainMenu;
