import React, { Component } from 'react';
import PageTitle from '../components/h1';
import MainText from '../components/text';
import BoldText from '../components/boldText';
import SmallText from '../components/smallText';
import AccentText from '../components/accentText';
import Button from '../components/button';
import Tile from '../components/tile';
import Coin from '../components/coin';
import Logo from '../components/logo';
import '../index.css';

class MainMenu extends Component {
  render() {
    return (
      <div className="wrapper">
        <PageTitle label="Bitcoin Miner ⛏" />
        <AccentText label="Tap as much, as you can" />
        <MainText label="Enter your name" />
        <MainText label="Field size:" />
        <MainText label="Game duration:" />
        <MainText label="Yield:" />
        <MainText label="Music:" />
        <MainText label="Volume level:" />
        <BoldText label="жирный текст" />

        <SmallText label="мелкий текст" />
        <Button label="Stats" />
        <Button label="Start game" />
        <Tile class="game-menu-tile" />
        <Coin class="game-menu-coin" />
        <Logo class="logo-footer" />
      </div>
    );
  }
}

export default MainMenu;
