import React, { Component } from 'react';
import WholeTile from '../components/wholeTile';
import Tile from '../components/Tile';
import Randomize from '../components/arrayRandomizer';
import Text from '../components/mainText';

import '../index.css';

// const fieldsize = () => {
//   if (!localStorage.size) {
//     return fieldsize = 120;
//   } else if(localStorage.size === 0) {
//     return fieldsize = 120;
//   } else if(localStorage.size === 1) {
//     return fieldsize = 180;
//   } else if(localStorage.size === 0) {
//     return fieldsize = 240;
// };

// const yeildRate = () => {
//   if (!localStorage.yeild) {
//     return fieldsize = 100;
//   } else if(localStorage.yeild === 0) {
//     return fieldyeild = 0.5;
//   } else if(localStorage.size === 1) {
//     return fieldsyeild = 0.7;
//   } else if(localStorage.size === 0) {
//     return yeild = 0.9;
// };

// const full = fieldsize * yeildRate;
// const empty = fieldsize - full;

const field = Randomize(120, 120);
//положить потом в state
class GameField extends Component {
  constructor() {
    super();
    this.state = {
      BTC: 0,
      field: field,
    };
  }

  handler = () => {
    this.setState({ BTC: this.state.BTC + 1 });
  };

  // handleClick(e) {
  //   e.preventDefault();
  //   console.log('click from gamefiled');
  // }

  render() {
    return (
      <div>
        <div className="raw">
          <Text label="Y&#39;ve mined:" classprop="main-text" />
          <Text label={this.state.BTC} classprop="bold-text" />
        </div>

        <div className="game-field">
          {this.state.field.map((item, index) =>
            item === 0 ? (
              <Tile key={index} />
            ) : (
              <WholeTile key={index} handler={this.handler} />
            )
          )}
        </div>
      </div>
    );
  }
}

export default GameField;
