import React, { Component } from 'react';
import '../index.css';
import Text from '../components/mainText';
import Dropdown from '../components/dropdown';

class FieldSetting extends React.Component {
  constructor() {
    super();
    this.state = {
      time: 30000,
      size: 200,
      yeild: 0.6,
    };
  }

  onTimeChange(e) {
    // this.setState({ time: e.target.value });
    // localStorage.setItem('time', this.state.time);
    console.log('onTimeChange');
  }

  handleSubmitTime(e) {
    // localStorage.setItem('time', this.state.time);
    // console.log('handleSubmitTime');
    this.setState({ time: e.target.value });
    console.log(this.state.time);
  }

  render() {
    const time = [30, 45, 90];
    const fieldSize = ['small', 'normal', 'large'];
    const yeild = ['Mongolian Steppe', 'European Average', 'Belorusian Magic'];
    return (
      <div className="section">
        <form className="raw" onChange={(e) => this.handleSubmitTime(e)}>
          <label className="main-text">Time:</label>
          <Dropdown
            data={time}
            id="GET-time"
            onChange={(e) => this.onTimeChange(e)}
          />
        </form>
        <form className="raw">
          <label className="main-text">Field size:</label>
          <Dropdown data={fieldSize} id="GET-size" />
        </form>
        <form className="raw">
          <label className="main-text">Yeild:</label>
          <Dropdown data={yeild} id="GET-yeild" />
        </form>
      </div>
    );
  }
}
export default FieldSetting;
