import React, { Component } from 'react';
import '../index.css';
import Text from '../components/mainText';
import Dropdown from '../components/dropdown';
import setFieldSize from '../components/setFiledSize';

class FieldSetting extends React.Component {
  constructor() {
    super();
    this.state = {
      time: 30000,
      size: 200,
      yeild: 0.6,
    };
  }

  handleSubmitTime(e) {
    localStorage.setItem('time');
  }

  handleSubmitSize(e) {
    localStorage.setItem('size', e.target.value);
  }

  handleSubmitYeild(e) {
    localStorage.setItem('yeild', e.target.value);
  }

  render() {
    const time = [30, 45, 90];
    const fieldSize = ['small', 'normal', 'large'];

    const yeild = ['Mongolian Steppe', 'European Average', 'Belorusian Magic'];
    return (
      <div className="section">
        <form className="raw" onChange={(e) => this.handleSubmitTime(e)}>
          <label className="main-text">Time:</label>
          <Dropdown data={time} id="GET-time" />
        </form>
        <form className="raw" onChange={(e) => this.handleSubmitSize(e)}>
          <label className="main-text">Field size:</label>
          <Dropdown data={fieldSize} id="GET-size" />
        </form>
        <form className="raw" onChange={(e) => this.handleSubmitYeild(e)}>
          <label className="main-text">Yeild:</label>
          <Dropdown data={yeild} id="GET-yeild" />
        </form>
      </div>
    );
  }
}
export default FieldSetting;
