import React, { Component } from 'react';
import '../index.css';
import Text from '../components/mainText';
import Dropdown from '../components/dropdown';

class FieldSetting extends React.Component {
  constructor() {
    super();
    this.state = {
      time: 30000,
      size: 200,
      yeild: 0.6,
    };
  }

  onTimeChange(e) {
    this.setState({ time: e.target.value });
  }

  handleSubmitTime(e) {
    localStorage.setItem('time', this.state.time);
  }

  handleSubmitSize() {
    localStorage.setItem('size', this.state.name);
  }

  handleSubmitYeild() {
    localStorage.setItem('yeild', this.state.name);
  }

  render() {
    const time = [30, 45, 90];
    const fieldSize = ['small', 'normal', 'large'];
    const yeild = ['Mongolian Steppe', 'European Average', 'Belorusian Magic'];
    return (
      <div className="section">
        <form className="raw" onSubmit={(e) => this.handleSubmitTime()}>
          <label for="GET-time" className="main-text">
            Time:
          </label>
          <Dropdown
            data={time}
            id="GET-time"
            onChange={() => this.onChangeTime(e)}
          />
        </form>
        <form className="raw">
          <label for="GET-time" className="main-text">
            Field size:
          </label>
          <Dropdown data={fieldSize} id="GET-size" />
        </form>
        <form className="raw">
          <label for="GET-time" className="main-text">
            Yeild:
          </label>
          <Dropdown data={yeild} id="GET-yeild" />
        </form>
      </div>
    );
  }
}
export default FieldSetting;
