import React, { Component } from 'react';
import WholeTile from '../components/wholeTile';
import Tile from '../components/tile';
import Randomizer from '../components/arrayRandomizer';
import BoldText from '../components/boldText';
import MainText from '../components/text';

import '../index.css';

const field = Randomizer(80, 120);
const BTCcounter = 0;

class GameField extends Component {
  constructor() {
    super();
  }

  updateCounter(BTCcounter) {
    return BTCcounter++;
  }

  render() {
    return (
      <div>
        <div className="settings-item">
          <MainText label="Y&#39;ve mined:" />
          <BoldText label={BTCcounter} />
        </div>
        <div className="game-field">
          {field.map((item, index) =>
            item === '0' ? (
              <Tile key={index} classprop="game-field-tile" />
            ) : (
              <WholeTile
                key={index}
                onClick={() => {
                  console.log('full tile click');
                }}
              />
            )
          )}
        </div>
      </div>
    );
  }
}

export default GameField;
